import logo from '../img/logo.PNG';
import React from 'react';
import ReactDOM from 'react-dom';
import { Layout, Menu,Breadcrumb} from 'antd';
import '.././index.css';
import Intro from './Intro';
import Home from './Home';

import History from './History';
import Works from './Works';
import {Route, Switch, HashRouter, Link} from 'react-router-dom';
import{
  PictureOutlined,
  HomeOutlined,
  VideoCameraOutlined,
  MessageOutlined
} from '@ant-design/icons';


const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

export default class HomePage extends React.Component {
  state = {
    collapsed: true,
  };

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { collapsed } = this.state;
    return (
      <HashRouter>
          <Layout style={{ minHeight: '100vh' }}>
          <Sider theme="light" collapsible collapsed={collapsed} onCollapse={this.onCollapse}>
            <div className="logo" />
            <Menu defaultSelectedKeys={['1']} mode="inline">
              <Menu.Item icon={<HomeOutlined />}>
              <Link to = "/首页" >首页</Link>
              </Menu.Item>
              <SubMenu icon={<PictureOutlined />} title="主题作品">
                <Link to = "/主题作品" ></Link>
                <Menu.Item key="3">主题作品1</Menu.Item>
                <Menu.Item key="4">主题作品2</Menu.Item>
                <Menu.Item key="5">主题作品3</Menu.Item>
              </SubMenu>
              <Menu.Item  icon={<VideoCameraOutlined />}>
                <Link to = "/历史回顾" >历史回顾</Link>
              </Menu.Item>
              {/* <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
                <Menu.Item key="6">Team 1</Menu.Item>
                <Menu.Item key="8">Team 2</Menu.Item>
              </SubMenu> */}
                <Menu.Item icon={<MessageOutlined />}>
                  西立画廊
                  <Link to = "/西立画廊" ></Link>
                </Menu.Item>
            </Menu>
          </Sider>
            <Layout className="site-layout">
              <Header className="site-layout-background" style={{ padding: 0, height:"100px"}}>
                <div className="logo">
                  <img src={logo} style={{height: "100px"}}/>
                  <span>西立画廊</span>
                </div>

              </Header>
              <Content style={{ margin: '0 16px', height:"80%" }}>

                <Breadcrumb style={{ margin: '16px 0' }}>
                  <Breadcrumb.Item>User</Breadcrumb.Item>
                  <Breadcrumb.Item>Bill</Breadcrumb.Item>
                </Breadcrumb>
                <Switch>
                  {/* <Route path="/aaa" component={Content1}> </Route> */}
                  <Route path="/首页" component={Home}> </Route>
                  <Route exact path="/主题作品" component={Works}> </Route>
                  <Route exact path="/历史回顾" component={History}> </Route>
                  <Route exact path="/西立画廊" component={Intro}> </Route>
                </Switch>
              </Content>
              <Footer style={{ textAlign: 'center' }}>Ant Design ©2018 Created by Ant UED</Footer>
            </Layout>
          </Layout>
      </HashRouter>

    );
  }
}
