import logo from '../img/logo.PNG';
import React from 'react';
import ReactDOM from 'react-dom';
import { Layout, Menu,Breadcrumb} from 'antd';
import '.././index.css';
import Intro from './Intro';
import Home from './Home';

import History from './History';
import Works from './Works';
import {Route, Switch, HashRouter, Link} from 'react-router-dom';
import{
  PictureOutlined,
  HomeOutlined,
  VideoCameraOutlined,
  MessageOutlined,
  SettingOutlined,
  TeamOutlined,
  CrownOutlined
} from '@ant-design/icons';
import SizeContext from 'antd/lib/config-provider/SizeContext';


const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

export default class HomePage extends React.Component {
  state = {
    collapsed: true,
  };

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { collapsed } = this.state;
    return (
      <HashRouter>
          <Layout style={{ minHeight: '100vh' }}>
          <Sider theme="light" collapsible collapsed={collapsed} onCollapse={this.onCollapse}>
            <div className="logo" />
            <Menu defaultSelectedKeys={['1']} mode="inline">
              <Menu.Item icon={<HomeOutlined />} >
              <Link to = "/HomePage" >HomePage</Link>
              </Menu.Item>
              <SubMenu icon={<CrownOutlined />} title="Customize Recipe">
                <Link to = "/CustomizeRecipe" ></Link>
                <Menu.Item key="3">Select Ingredients</Menu.Item>
                <Menu.Item key="4">Recipes Generated</Menu.Item>
                <Menu.Item key="5">Your Recipe</Menu.Item>
              </SubMenu>
              <Menu.Item  icon={<TeamOutlined />}>
                <Link to = "/MyAccount" >MyAccount</Link>
              </Menu.Item>
              {/* <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
                <Menu.Item key="6">Team 1</Menu.Item>
                <Menu.Item key="8">Team 2</Menu.Item>
              </SubMenu> */}
                <Menu.Item icon={<SettingOutlined />}>
                  <Link to = "/Settings" >Settings</Link>
                </Menu.Item>
            </Menu>
          </Sider>
            <Layout className="site-layout">
              <Header className="site-layout-background" style={{ padding: 0, height:"40px"}}>
                {/* <div className="logo">
                  <img src={logo} style={{height: "100px"}}/>
                  <span>西立画廊</span>
                </div> */}
                <div>
                  <h1 className="header_Frigo">FRIGO</h1>
                </div>

                <span style={{height:"20px"}}>FRIGO</span>

              </Header>
              <Content style={{ margin: '0 10px', height:"100%" }}>
                <Breadcrumb style={{ margin: '10px 0' }}>
                  <Breadcrumb.Item>User</Breadcrumb.Item>
                  <Breadcrumb.Item>Bill</Breadcrumb.Item>
                </Breadcrumb>
                <Switch>
                  {/* <Route path="/aaa" component={Content1}> </Route> */}
                  <Route exact path="/HomePage" component={Home}> </Route>
                  <Route path="/CustomizeRecipe" component={Works}> </Route>
                  <Route path="/MyAccount" component={History}> </Route>
                  <Route path="/Settings" component={Intro}> </Route>
                </Switch>
              </Content>
              <Footer style={{ textAlign: 'center' }}>Z9 ©2020 Created by UoM</Footer>
            </Layout>
          </Layout>
      </HashRouter>

    );
  }
}
